Â  Â  Â  Â  Â  {isAuthenticated ? (
Â  Â  Â  Â  Â  Â  <HeroCta to={isSeller ? "/my-shop" : "/shops"}>
Â  Â  Â  Â  Â  Â  Â  {isSeller ? "Manage My Shop" : "Explore Shops"}
Â  Â  Â  Â  Â  Â  </HeroCta>
Â  Â  Â  Â  Â  ) : (
Â  Â  Â  Â  Â  Â  <HeroCta to="/login">Log In</HeroCta>
Â  Â  Â  Â  Â  )}
Â  Â  Â  Â  </HeroContent>
Â  Â  Â  Â  <HeroImageGrid
Â  Â  Â  Â  Â  initial={{ opacity: 0, scale: 0.9 }}
Â  Â  Â  Â  Â  animate={{ opacity: 1, scale: 1 }}
Â  Â  Â  Â  Â  transition={{ duration: 0.5, delay: 0.2 }}
Â  Â  Â  Â  >
Â  Â  Â  Â  Â  <HeroImageItem className="main">
Â  Â  Â  Â  Â  Â  <img src={hamburgerImg} alt="Hamburger" />
Â  Â  Â  Â  Â  </HeroImageItem>
Â  Â  Â  Â  Â  <HeroImageItem>
Â  Â  Â  Â  Â  Â  <img src={pizzaSliceImg} alt="Pizza Slice" />
Â  Â  Â  Â  Â  </HeroImageItem>
Â  Â  Â  Â  Â  <HeroImageItem>
Â  Â  Â  Â  Â  Â  <img src={pastaImg} alt="Pasta" />
Â  Â  Â  Â  Â  </HeroImageItem>
Â  Â  Â  Â  Â  <HeroImageItem>
Â  Â  Â  Â  Â  Â  <img src={caesarSaladImg} alt="Caesar Salad" />
Â  Â  Â  Â  Â  </HeroImageItem>
Â  Â  Â  Â  Â  <HeroImageItem>
Â  Â  Â  Â  Â  Â  <img src={chocolateCakeImg} alt="Chocolate Cake" />
Â  Â  Â  Â  Â  </HeroImageItem>
Â  Â  Â  Â  </HeroImageGrid>
Â  Â  Â  </HeroSection>

Â  Â  Â  <FeaturesSection>
