import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import {
  FiArrowRight,
  FiShoppingBag,
  FiClock,
  FiCheckCircle,
  FiShoppingCart,
  FiUsers,
  FiList,
  FiPlusCircle,
  FiLogIn,
  FiUserPlus,
} from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';
import AnimatedBackground from '../components/AnimatedBackground';

// Import food images for the hero section
import hamburgerImg from '../assets/images/Hamburger.jpg';
import pizzaSliceImg from '../assets/images/Pizza Slice.jpg';
import pastaImg from '../assets/images/Pasta.jpg';
import caesarSaladImg from '../assets/images/Caesar Salad.jpg';
import chocolateCakeImg from '../assets/images/Chocolate Cake.jpg';

const HomePage = () => {
  const { isAuthenticated, currentUser, isSeller, isCustomer } = useAuth();

  return (
    <Container>
      <AnimatedBackground theme="food" />
      <HeroSection>
        <HeroContent
          initial={{ opacity: 0, y: 25 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, ease: 'easeOut' }}
        >
          <HeroTitle>
            {isAuthenticated
              ? `Welcome back, ${currentUser?.name || 'User'}!`
              : 'Welcome to WildEats Online Canteen'}
          </HeroTitle>
          <HeroSubtitle>
            {isAuthenticated
              ? isSeller
                ? 'Manage your shop and fulfill orders'
                : 'Your one-stop solution for campus dining'
              : 'Your one-stop solution for campus dining'}
          </HeroSubtitle>
          <HeroDescription>
            {isAuthenticated
              ? isSeller
                ? 'Manage your shop, add food items, and process customer orders all in one place.'
                : 'Browse shops, place orders, and enjoy delicious meals from your favorite campus eateries.'
              : 'Log in to browse shops, place orders, and enjoy delicious meals from your favorite campus eateries.'}
          </HeroDescription>
          {isAuthenticated ? (
            <HeroCta to={isSeller ? '/my-shop' : '/shops'}>
              {isSeller ? 'Manage My Shop' : 'Explore Shops'}
              <FiArrowRight />
            </HeroCta>
          ) : (
            <HeroCta to="/login">
              Log In <FiArrowRight />
            </HeroCta>
          )}
        </HeroContent>
        <HeroImageGrid
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.6, delay: 0.3, ease: 'easeOut' }}
        >
          <HeroImageItem className="main">
            <img src={hamburgerImg} alt="Hamburger" />
          </HeroImageItem>
          <HeroImageItem>
            <img src={pizzaSliceImg} alt="Pizza Slice" />
          </HeroImageItem>
          <HeroImageItem>
            <img src={pastaImg} alt="Pasta" />
          </HeroImageItem>
          <HeroImageItem>
            <img src={caesarSaladImg} alt="Caesar Salad" />
          </HeroImageItem>
          <HeroImageItem>
            <img src={chocolateCakeImg} alt="Chocolate Cake" />
          </HeroImageItem>
        </HeroImageGrid>
      </HeroSection>

      <FeaturesSection>
        <SectionTitle>{isAuthenticated ? 'Quick Actions' : 'Get Started'}</SectionTitle>
        <FeaturesGrid>
          {!isAuthenticated ? (
            <>
              <FeatureCard as={Link} to="/login" whileHover={{ scale: 1.05 }} transition={{ duration: 0.3 }}>
                <FeatureIcon className="login">
                  <FiLogIn />
                </FeatureIcon>
                <FeatureTitle>Log In</FeatureTitle>
                <FeatureDescription>Access your account to place orders and view history</FeatureDescription>
              </FeatureCard>

              <FeatureCard as={Link} to="/register" whileHover={{ scale: 1.05 }} transition={{ duration: 0.3 }}>
                <FeatureIcon className="register">
                  <FiUserPlus />
                </FeatureIcon>
                <FeatureTitle>Register</FeatureTitle>
                <FeatureDescription>Create a new account to start using WildEats</FeatureDescription>
              </FeatureCard>
            </>
          ) : isSeller ? (
            <>
              <FeatureCard as={Link} to="/my-shop" whileHover={{ scale: 1.05 }} transition={{ duration: 0.3 }}>
                <FeatureIcon className="shop">
                  <FiShoppingBag />
                </FeatureIcon>
                <FeatureTitle>My Shop</FeatureTitle>
                <FeatureDescription>Manage your shop details and food items</FeatureDescription>
              </FeatureCard>

              <FeatureCard as={Link} to="/orders" whileHover={{ scale: 1.05 }} transition={{ duration: 0.3 }}>
                <FeatureIcon className="orders">
                  <FiList />
                </FeatureIcon>
                <FeatureTitle>Incoming Orders</FeatureTitle>
                <FeatureDescription>View and process customer orders</FeatureDescription>
              </FeatureCard>
            </>
          ) : (
            <>
              <FeatureCard as={Link} to="/shops" whileHover={{ scale: 1.05 }} transition={{ duration: 0.3 }}>
                <FeatureIcon className="shopping">
                  <FiShoppingCart />
                </FeatureIcon>
                <FeatureTitle>Browse Shops</FeatureTitle>
                <FeatureDescription>Explore all available canteen shops and their offerings</FeatureDescription>
              </FeatureCard>

              <FeatureCard as={Link} to="/orders" whileHover={{ scale: 1.05 }} transition={{ duration: 0.3 }}>
                <FeatureIcon className="orders">
                  <FiList />
                </FeatureIcon>
                <FeatureTitle>View Orders</FeatureTitle>
                <FeatureDescription>Check the status of your current and past orders</FeatureDescription>
              </FeatureCard>

              <FeatureCard as={Link} to="/create-order" whileHover={{ scale: 1.05 }} transition={{ duration: 0.3 }}>
                <FeatureIcon className="plus">
                  <FiPlusCircle />
                </FeatureIcon>
                <FeatureTitle>Place Order</FeatureTitle>
                <FeatureDescription>Quickly place a new order from your favorite shops</FeatureDescription>
              </FeatureCard>
            </>
          )}
        </FeaturesGrid>
      </FeaturesSection>

      <CallToActionSection>
        <CtaContainer>
          <CtaImageContainer>
            <CtaImage src={chocolateCakeImg} alt="Delicious dessert" />
          </CtaImageContainer>
          <CtaText>
            <CtaTitle>Ready to Order?</CtaTitle>
            <CtaDescription>
              Explore our menu and place your order now for a delightful dining experience.
            </CtaDescription>
            <CtaButton to="/create-order">Place an Order</CtaButton>
          </CtaText>
        </CtaContainer>
      </CallToActionSection>

      <ManagementSection>
        <SectionTitle>Management Features</SectionTitle>

        <ManagementGrid>
          <ManagementCard
            whileHover={{ y: -6, boxShadow: '0 12px 28px rgba(0, 0, 0, 0.18)' }}
            transition={{ type: 'spring', stiffness: 280 }}
            to="/shops"
          >
            <ManagementIcon className="shop">
              <FiShoppingCart />
            </ManagementIcon>
            <ManagementTitle>Shop Management</ManagementTitle>
            <ManagementDescription>
              Add, edit, and manage shops in the online canteen system.
            </ManagementDescription>
          </ManagementCard>

          <ManagementCard
            whileHover={{ y: -6, boxShadow: '0 12px 28px rgba(0, 0, 0, 0.18)' }}
            transition={{ type: 'spring', stiffness: 280 }}
            to="/users"
          >
            <ManagementIcon className="user">
              <FiUsers />
            </ManagementIcon>
            <ManagementTitle>User Management</ManagementTitle>
            <ManagementDescription>
              Manage user accounts, permissions, and profiles.
            </ManagementDescription>
          </ManagementCard>
        </ManagementGrid>
      </ManagementSection>
    </Container>
  );
};

const Container = styled.div`
  max-width: 100%;
  overflow-x: hidden;
  position: relative;
  z-index: 1;
  background: var(--bg-light);
`;

// Hero Section styles
const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 4rem 1.5rem;
  background: rgba(255 255 255 / 0.82);
  backdrop-filter: saturate(180%) blur(14px);
  border-radius: var(--border-radius);
  box-shadow: 0 12px 40px rgba(0, 0, 0, 0.12);
  margin: 3rem 1rem;

  @media (min-width: 992px) {
    flex-direction: row;
    justify-content: space-between;
    gap: 3rem;
    min-height: 75vh;
    max-width: 1200px;
    margin: 3rem auto;
  }
`;

const HeroContent = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  margin-bottom: 2.5rem;

  @media (min-width: 992px) {
    align-items: flex-start;
    text-align: left;
    width: 50%;
    margin-bottom: 0;
  }
`;

const HeroTitle = styled.h1`
  font-size: 2.75rem;
  font-weight: 800;
  color: var(--primary);
  margin-bottom: 1.25rem;
  line-height: 1.1;
  text-shadow: 0 3px 8px rgba(0, 0, 0, 0.12);

  @media (min-width: 768px) {
    font-size: 4rem;
  }
`;

const HeroSubtitle = styled.p`
  font-size: 1.3rem;
  color: var(--text-dark);
  margin-bottom: 2rem;
  max-width: 480px;
  font-weight: 600;
`;

const HeroDescription = styled.p`
  font-size: 1.05rem;
  color: var(--text-secondary);
  margin-bottom: 2.5rem;
  max-width: 600px;
  line-height: 1.6;
  font-weight: 400;
`;

const HeroCta = styled(Link)`
  display: inline-flex;
  align-items: center;
  gap: 0.75rem;
  background-color: var(--primary);
  color: var(--white);
  padding: 0.85rem 2rem;
  border-radius: var(--border-radius);
  font-weight: 600;
  font-size: 1.1rem;
  box-shadow: 0 6px 18px rgba(0, 0, 0, 0.18);
  transition: all 0.3s ease;

  svg {
    transition: transform 0.35s ease;
    font-size: 1.25rem;
  }

  &:hover {
    background-color: var(--primary-light);
    transform: translateY(-4px);
    box-shadow: 0 10px 24px rgba(0, 0, 0, 0.25);
  }

  &:hover svg {
    transform: translateX(6px);
  }
`;

const HeroImageGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(3, 1fr);
  gap: 16px;
  width: 100%;
  max-width: 520px;
  border-radius: var(--border-radius);
  overflow: hidden;
  box-shadow: 0 14px 44px rgba(0, 0, 0, 0.16);
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: scale(1.04);
    box-shadow: 0 18px 48px rgba(0, 0, 0, 0.22);
  }

  @media (min-width: 992px) {
    width: 50%;
  }
`;

const HeroImageItem = styled.div`
  border-radius: var(--border-radius);
  overflow: hidden;
  height: 160px;

  &.main {
    grid-column: span 2;
    grid-row: span 2;
    height: 320px;
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
    transition: transform 0.45s ease;
  }

  &:hover img {
    transform: scale(1.08);
  }
`;

// Features Section styles
const FeaturesSection = styled.section`
  padding: 4rem 1.5rem;
  background-color: rgba(245 245 245 / 0.85);
  backdrop-filter: saturate(180%) blur(12px);
  margin: 3rem 1rem;
  border-radius: var(--border-radius);
  box-shadow: 0 10px 38px rgba(0, 0, 0, 0.12);

  @media (min-width: 992px) {
    margin: 3rem auto;
    max-width: 1200px;
  }
`;

const SectionTitle = styled.h2`
  font-size: 2.25rem;
  text-align: center;
  color: var(--text-dark);
  margin-bottom: 3.5rem;
  font-weight: 700;
  text-shadow: 0 3px 7px rgba(0, 0, 0, 0.08);
  position: relative;

  &:after {
    content: '';
    position: absolute;
    bottom: -14px;
    left: 50%;
    transform: translateX(-50%);
    width: 70px;
    height: 4px;
    background-color: var(--primary);
    border-radius: 5px;
  }
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 2.5rem;
  max-width: 1200px;
  margin: 0 auto;

  @media (min-width: 768px) {
    grid-template-columns: repeat(3, 1fr);
  }
`;

const FeatureCard = styled(motion.div)`
  background-color: rgba(255 255 255 / 0.9);
  padding: 2.2rem 2.2rem 1.5rem;
  border-radius: var(--border-radius);
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.10);
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  backdrop-filter: blur(5px);
  transition: all 0.35s ease;
  cursor: pointer;
  user-select: none;

  &:hover {
    background-color: rgba(255 255 255 / 0.98);
    box-shadow: 0 14px 38px rgba(0, 0, 0, 0.14);
  }
`;

const FeatureIcon = styled.div`
  width: 4.4rem;
  height: 4.4rem;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 1.75rem;
  font-size: 1.85rem;
  color: var(--primary);

  &.login {
    background-color: rgba(25 118 210 / 0.15);
    color: #1976d2;
  }
  &.register {
    background-color: rgba(102 51 153 / 0.15);
    color: #663399;
  }
  &.shop {
    background-color: rgba(255 87 34 / 0.15);
    color: #ff5722;
  }
  &.shopping {
    background-color: rgba(128 0 32 / 0.15);
    color: var(--primary);
  }
  &.orders {
    background-color: rgba(33 150 243 / 0.15);
    color: #2196f3;
  }
  &.plus {
    background-color: rgba(76 175 80 / 0.15);
    color: #4caf50;
