import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiArrowRight, FiShoppingBag, FiShoppingCart, FiUsers, FiList, FiPlusCircle, FiLogIn, FiUserPlus, FiSearch } from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';
import AnimatedBackground from '../components/AnimatedBackground';

import hamburgerImg from '../assets/images/Hamburger.jpg';
import pizzaSliceImg from '../assets/images/Pizza Slice.jpg';
import pastaImg from '../assets/images/Pasta.jpg';
import caesarSaladImg from '../assets/images/Caesar Salad.jpg';
import chocolateCakeImg from '../assets/images/Chocolate Cake.jpg';

const HomePage = () => {
  const { isAuthenticated, currentUser, isSeller, isCustomer } = useAuth();

  const heroCtaPath = isAuthenticated ? (isSeller ? '/my-shop' : '/shops') : '/login';
  const heroCtaText = isAuthenticated ? (isSeller ? 'Manage My Shop' : 'Explore Shops') : 'Log In';

  return (
    <Page>
      <AnimatedBackground theme="food" subtle />

      <TopBar>
        <Brand to="/">WildEats</Brand>
        <TopActions>
          <Search role="search">
            <FiSearch aria-hidden="true" />
            <input placeholder="Search shops, dishes..." aria-label="Search" />
          </Search>

          {!isAuthenticated ? (
            <AuthLinks>
              <SmallLink to="/login"><FiLogIn /> Sign in</SmallLink>
              <SmallLink to="/register"><FiUserPlus /> Register</SmallLink>
            </AuthLinks>
          ) : (
            <UserChip>
              <FiUsers />
              <span>{currentUser?.name || 'You'}</span>
            </UserChip>
          )}
        </TopActions>
      </TopBar>

      <Main>
        <Hero>
          <HeroLeft as={motion.div} initial={{ opacity: 0, x: -10 }} animate={{ opacity: 1, x: 0 }} transition={{ duration: 0.5 }}>
            <HeroEyebrow>Campus Canteen · Fresh · Local</HeroEyebrow>
            <HeroTitle>
              {isAuthenticated ? `Welcome back, ${currentUser?.name || 'friend'}!` : 'Hungry? We got you.'}
            </HeroTitle>
            <HeroSubtitle>
              {isAuthenticated
                ? isSeller
                  ? 'Run your shop, update menu items and fulfill orders from students nearby.'
                  : 'Discover campus shops and order food for pickup or delivery.'
                : 'Browse shops and menus. Sign in to place orders and track them easily.'}
            </HeroSubtitle>

            <HeroButtons>
              <PrimaryCta to={heroCtaPath} aria-label={heroCtaText}>{heroCtaText} <FiArrowRight /></PrimaryCta>
              <OutlineCta to="/shops">Browse shops</OutlineCta>
            </HeroButtons>

            <QuickStats>
              <Stat>
                <strong>120+</strong>
                <span>Shops</span>
              </Stat>
              <Divider />
              <Stat>
                <strong>2k+</strong>
                <span>Orders today</span>
              </Stat>
              <Divider />
              <Stat>
                <strong>4.9</strong>
                <span>Avg rating</span>
              </Stat>
            </QuickStats>
          </HeroLeft>

          <HeroRight as={motion.div} initial={{ opacity: 0, scale: 0.98 }} animate={{ opacity: 1, scale: 1 }} transition={{ duration: 0.5 }}>
            <ImageGrid>
              <ImageItem className="large"><img src={hamburgerImg} alt="Hamburger" /></ImageItem>
              <ImageItem><img src={pizzaSliceImg} alt="Pizza" /></ImageItem>
              <ImageItem><img src={pastaImg} alt="Pasta" /></ImageItem>
              <ImageItem><img src={caesarSaladImg} alt="Salad" /></ImageItem>
              <ImageItem><img src={chocolateCakeImg} alt="Dessert" /></ImageItem>
            </ImageGrid>
          </HeroRight>
        </Hero>

        <Features>
          <SectionHeader>
            <h2>{isAuthenticated ? 'Quick Actions' : 'Get Started'}</h2>
            <p>Fast paths to the things you do most.</p>
          </SectionHeader>

          <Cards>
            {!isAuthenticated ? (
              <>
                <CardLink to="/login">
                  <IconCircle><FiLogIn /></IconCircle>
                  <h3>Sign in</h3>
                  <p>Access your account to place orders and manage preferences.</p>
                </CardLink>

                <CardLink to="/register">
                  <IconCircle><FiUserPlus /></IconCircle>
                  <h3>Create account</h3>
                  <p>Create a profile to start ordering and saving favorites.</p>
                </CardLink>

                <CardFake>
                  <IconCircle><FiShoppingBag /></IconCircle>
                  <h3>Explore menu</h3>
                  <p>Peek at popular items and chef picks—even before signing in.</p>
                </CardFake>
              </>
            ) : isSeller ? (
              <>
                <CardLink to="/my-shop">
                  <IconCircle><FiShoppingBag /></IconCircle>
                  <h3>My shop</h3>
                  <p>Manage your shop, update menu and view sales.</p>
                </CardLink>

                <CardLink to="/orders">
                  <IconCircle><FiList /></IconCircle>
                  <h3>Orders</h3>
                  <p>View and fulfill incoming orders quickly.</p>
                </CardLink>

                <CardLink to="/create-order">
                  <IconCircle><FiPlusCircle /></IconCircle>
                  <h3>Add item</h3>
                  <p>Quick add a new menu item to your shop.</p>
                </CardLink>
              </>
            ) : (
              <>
                <CardLink to="/shops">
                  <IconCircle><FiShoppingCart /></IconCircle>
                  <h3>Browse shops</h3>
                  <p>Explore by category, rating, or what's nearby.</p>
                </CardLink>

                <CardLink to="/orders">
                  <IconCircle><FiList /></IconCircle>
                  <h3>My orders</h3>
                  <p>Track progress and reorder past favorites.</p>
                </CardLink>

                <CardLink to="/create-order">
                  <IconCircle><FiPlusCircle /></IconCircle>
                  <h3>Place order</h3>
                  <p>Quick-order from favorite shops in less than a minute.</p>
                </CardLink>
              </>
            )}
          </Cards>
        </Features>

        <CtaBanner>
          <CtaCard>
            <CtaImg src={chocolateCakeImg} alt="Dessert" />
            <div>
              <h3>Ready to eat?</h3>
              <p>Place your order now and pick up from campus shops nearby.</p>
              <CtaActions>
                <PrimaryCta to="/create-order">Place an order <FiArrowRight /></PrimaryCta>
                <OutlineCta to="/shops">Browse shops</OutlineCta>
              </CtaActions>
            </div>
          </CtaCard>
        </CtaBanner>

        <Management>
          <SectionHeader>
            <h2>Management</h2>
            <p>Admin tools and moderation.</p>
          </SectionHeader>

          <ManagementGrid>
            <ManagementCard to="/shops">
              <ManagementIcon className="shop"><FiShoppingCart /></ManagementIcon>
              <h4>Shop management</h4>
              <p>Add, edit and moderate shops and menus.</p>
            </ManagementCard>

            <ManagementCard to="/users">
              <ManagementIcon className="user"><FiUsers /></ManagementIcon>
              <h4>User management</h4>
              <p>Manage user roles, verifications and reports.</p>
            </ManagementCard>
          </ManagementGrid>
        </Management>
      </Main>

      <Footer>
        <FooterInner>
          <div>© {new Date().getFullYear()} WildEats</div>
          <div>
            <FooterLink to="/terms">Terms</FooterLink>
            <FooterLink to="/privacy">Privacy</FooterLink>
          </div>
        </FooterInner>
      </Footer>
    </Page>
  );
};

// Styled components
const Page = styled.div`
  min-height: 100vh;
  position: relative;
  font-family: Inter, system-ui, -apple-system, 'Segoe UI', Roboto, 'Helvetica Neue', Arial;
  color: var(--text-dark);
`;

const TopBar = styled.header`
  max-width: 1200px;
  margin: 1rem auto;
  padding: 0 1rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 1rem;
`;

const Brand = styled(Link)`
  font-weight: 800;
  font-size: 1.25rem;
  color: var(--primary);
  text-decoration: none;
`;

const TopActions = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const Search = styled.label`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  background: rgba(255,255,255,0.85);
  padding: 0.35rem 0.6rem;
  border-radius: 999px;
  box-shadow: 0 6px 20px rgba(0,0,0,0.06);
  input { border: none; background: transparent; outline: none; min-width: 180px; }
  svg { color: #777; }
`;

const AuthLinks = styled.div`
  display:flex; gap:0.5rem; align-items:center;
